mapper.select:
SELECT

FROM oomall_goods
WHERE
state != 2
and id = #{id} 
and goods_sn = #{goodsSn} 
and name = #{name} 
and category_id = #{categoryId} 
and brand_id = #{brandId} 
and state = #{state}

mapper.update:
UPDATE oomall_goods
name = #{name}, category_id = #{categoryId}, brand_id = #{brandId}, brief = #{brief}, spec_list = #{specList}, pic_url = #{picUrl}, unit = #{unit}, state = #{state}, update_time = CURRENT_TIME, modi_user = #{modiUser}
WHERE id = #{id}

UPDATE oomall_goods
SET state = 2
WHERE id = #{id}

UPDATE oomall_product
SET state = 2
WHERE goods_id = #{id}

UPDATE oomall_product
SET state = 2
WHERE id = #{id}
